import React from 'react'
import { useEffect, useState } from 'react'

const CreatePostForm = ({ currentUser, post }) => {
  const [formValues, setFormValues] = useState({
    title: "",
    text: "",
    img_url: "",
    video_url: "",
    caption: ""
  })

  const handleChange = (e) => {
    setFormValues({ ...formValues, [e.target.id]: e.target.value })
  }

      return (
        <div>
          <div className="user-nav-dropdown">
            <button className="dropbtn">
              <div className="gender">
                Add Media
              </div>
            </button>
            <div className="dropdown-content">
              <div className="user-profile">
                <h4>Image</h4>
              </div>

              <div className="user-profile">
                <h4>Video</h4>
              </div>
              <div>
                <h4>Written</h4>
              </div>
            </div>
          </div>
          <div className="bio">
            <div className="box-title">
              <input
                type="text"
                defaultValue={formValues.image_url}
                onChange={handleChange}
                id={'image_url'}
                placeholder={'Image URL'}
                className="input-box"
              />
              <textarea
                type="text"
                defaultValue={formValues.caption}
                onChange={handleChange}
                id={'caption'}
                placeholder={'Image Caption'}
                className="edit-bio-text"
              ></textarea>
            </div>
            <div className="post-text">
              <h4>{formValues.caption}</h4>
            </div>
          </div>
          <div className="bio">
            <div className="box-title">
              <input
                type="text"
                defaultValue={formValues.video_url}
                onChange={handleChange}
                id={'video_url'}
                placeholder={'Video URL'}
                className="input-box"
              />
              <textarea
                type="text"
                defaultValue={formValues.caption}
                onChange={handleChange}
                id={'caption'}
                placeholder={'Video Caption'}
                className="edit-bio-text"
              ></textarea>
              </div>
              <div className="post-text">
                <h4>{formValues.caption}</h4>
            </div>
          </div>
          <div className="bio">
              <div className="box-title">
                <input
                type="text"
                defaultValue={formValues.title}
                onChange={handleChange}
                id={'title'}
                placeholder={'Post Title'}
                className="input-box"
                />
                <textarea
                type="text"
                defaultValue={formValues.text}
                onChange={handleChange}
                id={'text'}
                placeholder={'Post Text'}
                className="edit-bio-text"
                ></textarea>
              </div>
              <div className="post-text">
                <h4>{formValues.text}</h4>
              </div>
          </div>
        </div>

      )
}

export default CreatePostForm
